function initBurgerMenu(){let t=document.querySelector(".header__burger"),s=document.querySelector(".header__nav"),c=document.querySelector(".header"),o=document.body;t.addEventListener("click",e=>{e.stopPropagation();e=t.classList.toggle("active");s.classList.toggle("active",e),c.classList.toggle("header--open",e),o.classList.toggle("no-scroll",e)}),document.addEventListener("click",e=>{s.contains(e.target)||t.contains(e.target)||s.classList.contains("active")&&(t.classList.remove("active"),s.classList.remove("active"),c.classList.remove("header--open"),o.classList.remove("no-scroll"))})}export{initBurgerMenu};